import { ACTIONS } from '../Actions/cartActions';

const initState = {
    isLoading: true,
    items: [],
    itemsInCart:[],
    cartValue: 0,
    error: null,
}
const cartReducer= (state = initState, action)=>{
    switch(action.type){
        case ACTIONS.RESPONSE_FROM_API:
            return {
                ...state,
                items: action.data,
                isLoading: false
            };
        case ACTIONS.FAILED_TO_FETCH_API_RESPONSE:
            return {
                ...state,
                error: action.error,
                isLoading: false
            };
        case ACTIONS.ADD_PRODUCT_TO_CART: {
            const itemAdded = state.items.find(item => item.partId === action.id);
            const itemExistsInCart = state.itemsInCart.find(item => item.partId === action.id);
            if(itemExistsInCart){
                itemAdded.quantity += 1;
                return {
                    ...state,
                    cartValue: state.cartValue + itemAdded.priceValue
                }
            }
            else{
                itemAdded.quantity = 1;
                const newCartValue = state.cartValue + itemAdded.priceValue;
                return {
                    ...state,
                    itemsInCart: [...state.itemsInCart, itemAdded],
                    cartValue: newCartValue
                }
            }
        }
        case ACTIONS.INCREASE_ITEM_QUNATITY: {
            const itemAdded = state.items.find(item => item.partId === action.id);
            const newCartValue = state.cartValue + (itemAdded.priceValue * removedItem.quantity); 
            return {
                ...state,
                cartValue: newCartValue
            }
        }
        case ACTIONS.DECREASE_ITEM_QUNATITY: {
            const itemAdded = state.items.find(item => item.partId === action.id);
            const newCartValue = state.cartValue + (itemAdded.priceValue * removedItem.quantity); 
            return {
                ...state,
                cartValue: newCartValue
            }
        }
        case ACTIONS.REMOVE_ITEM_FROM_CART: {
            const removedItem = state.itemsInCart.find(item => item.partId === action.id);
            const updatedCartItems = state.itemsInCart.filter(item => item.partId !== action.id);
            const newCartValue = state.cartValue - (removedItem.priceValue * removedItem.quantity); 
            return {
                ...state,
                itemsInCart: updatedCartItems,
                cartValue: newCartValue
            }
        }
        default:
            return state;
    }
}

export default cartReducer;
