const initState = {
    isLoading: true,
    items: [],
    itemsInCart:[],
    cartValue: 0,
    error: null,
}
const cartReducer= (state = initState,action)=>{
    if(action.type === 'ADD_PRODUCT_TO_CART'){
    console.log("state.items", state.items);
        const itemAdded = state.items.find(item => item.partId === action.id);
        const itemExistsInCart = state.itemsInCart.find(item => item.partId === action.id);
        if(itemExistsInCart){
            itemAdded.quantity += 1;
            return {
                ...state,
                cartValue: state.cartValue + itemAdded.priceValue
            }
        }
        else{
            itemAdded.quantity = 1;
            const newCartValue = state.cartValue + itemAdded.priceValue;
            return{
                ...state,
                itemsInCart: [...state.itemsInCart, itemAdded],
                cartValue: newCartValue
            }
        }
    }
    else{
    console.log("state.items", state.items);
        return state;
    }   
}

export default cartReducer;
