//export const ADD_PRODUCT_TO_CART = 'ADD_PRODUCT_TO_CART';

export const responseFromApi = (data) => {
    return {type: 'RESPONSE_FROM_API', data};
}

export const failedToFetchApiResponse = (data) => {
    return {type: 'FAILED_TO_FETCH_API_RESPONSE', data};
}

export const addProductToCart = (id) => {
    return {type: 'ADD_PRODUCT_TO_CART', id};
}
