import React, { Component } from 'react';
import { connect } from 'react-redux';

class HomePage extends Component{
    state = {
        items: [],
        addedItems:[],
        total: 0
    };

    fetchUsers(){
        fetch('https://shopping-cart-demo-api.herokuapp.com/products')
        .then(resp => resp.json())
        .then(data => this.setState({ items: data }));
        console.log("state", this.state.items);
    }
    componentDidMount(){
        this.fetchUsers();
    }

    render(){
        const { items } = this.state;
        let itemList = items.map(item => {
            return(
                <div className="card" key={item.partId}>
                        <div className="card-image">
                            <img src={item.image2} alt={item.title}/>
                            <span className="card-title">{item.title}</span>
                            <span to="/" className="btn-floating halfway-fab waves-effect waves-light red"><i className="material-icons">add</i></span>
                        </div>

                        <div className="card-content">
                            <p>{item.description}</p>
                            <p><b>Price: {item.priceValue}$</b></p>
                        </div>
                 </div>
            );
        });
        return(
            <div className="container">
                <h3 className="center">Our items</h3>
                <div className="box">
                    {itemList}
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        items: state.items
    }
}
export default connect(mapStateToProps)(HomePage);
