import React, { Component } from 'react';
import { connect } from 'react-redux';

class HomePage extends Component{
    state = {
        items: [],
          addedItems:[],
          total: 0
    };
    render(){
        const url = 'https://shopping-cart-demo-api.herokuapp.com/products';
        let initState;
fetch(url)
  .then(resp => resp.json())
  .then(data => {
      initState = {
          items: data,
          addedItems:[],
          total: 0
      }
      console.log("items", initState.items);
    });
        let itemList = this.props.items.map(item => {
            return(
                <div className="card" key={item.id}>
                        <div className="card-image">
                            <img src={item.img} alt={item.title}/>
                            <span className="card-title">{item.title}</span>
                            <span to="/" className="btn-floating halfway-fab waves-effect waves-light red"><i className="material-icons">add</i></span>
                        </div>

                        <div className="card-content">
                            <p>{item.desc}</p>
                            <p><b>Price: {item.price}$</b></p>
                        </div>
                 </div>
            )
        });
        return(
            <div className="container">
                <h3 className="center">Our items</h3>
                <div className="box">
                    {itemList}
                </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        items: state.items
    }
}
export default connect(mapStateToProps)(HomePage);
