const initState = {
    isLoading: true,
    items: [],
    itemsInCart:[],
    cartValue: 0,
    error: null,
}
const cartReducer= (state = initState, action)=>{
    switch(action.type){
        case 'FETCH_FROM_API': {
            fetch(action.url)
            .then(resp => resp.json())
            .then(data => {
            return {
                ...state,
                items: data,
                isLoading: false
            }
        })
            .catch(error => {
            return {
                ...state,
                error: error,
                isLoading: false
            }
        })
        break;
        }
        default:
            return state;
    }

